           \&#34;price\&#34;: 100,
           \&#34;paymentProvider\&#34;: \&#34;XSOLLA\&#34;,
           \&#34;vat\&#34;: 0,
           \&#34;salesTax\&#34;: 0,
           \&#34;paymentProviderFee\&#34;: 0,
           \&#34;paymentMethodFee\&#34;: 0,
           \&#34;currency\&#34;: {
                   \&#34;currencyCode\&#34;: \&#34;USD\&#34;,
                   \&#34;currencySymbol\&#34;: \&#34;$\&#34;,
